import React, { useState } from 'react';
import "./PatientApppointmentForm.css";

function PatientAppointmentForm() {
  
  const [name, setName] = useState('');
  const [number, setNumber] = useState('');
  const [email, setEmail] = useState('');
  
  const [appointmentDate, setAppointmentDate] = useState('');
  const [symptoms, setSymptoms] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    
    setIsSubmitted(true);
       console.log({
      name,
      email,
      number,
      appointmentDate,
      symptoms,
    });

    // Optionally, reset form
    // setName('');
    // setEmail('');
    // setAppointmentDate('');
    // setSymptoms('');
  };

  return (
    <div id='main-div'>
      <h1 id='patient'>Patient Appointment Form</h1>
      {isSubmitted ? (
        <div id='content'>
          <h2>Appointment Submitted Successfully!</h2>
          <p>Thank you for scheduling your appointment.</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>

         <div id='main_container'>
          
             Name :
            <input
              type="text"
              id="name"
              placeholder='Enter Your Name'
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          
          <br/>

          
            Email :
            <input
              type="email"
              id="email"
              placeholder='Enter Your Email'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />

         </div>
             

             <div id='mini_container'>
          
              Phone No:
                 <input
              type="number"
              id="number"
              placeholder='Enter Your Number'
              value={number}
              onChange={(e) => setNumber(e.target.value)}
              required
            />

            <br/>
           Appointment Date:
            <input
              type="date"
              id="appointmentDate"
              value={appointmentDate}
              onChange={(e) => setAppointmentDate(e.target.value)}
              required
            />
              
              <br/>

              </div>

              <div id='symtom'>

              
             


          <label htmlFor="symptoms">Symptoms:</label>
            <textarea
              id="symptoms"
              value={symptoms}
              onChange={(e) => setSymptoms(e.target.value)}
            />

          
                 <br/>
          <button type="submit" id='submit'>Submit Appointment</button>
          </div>
        </form>
        
      )}
    </div>
  );
}

export default PatientAppointmentForm;
