import React, { useRef } from "react";
import "./Varify.css";

 const Varify = () => {
//     let inputAll=document.querySelectorAll('.inputs');
//     inputAll.forEach((input,index)=>{
//         input.addEventListener('keyup',(e)=>{
//             if (input.value.length == 1 && index < 3) {
//                 inputAll[index + 1].focus();
//               }
//               if(input.value.length==0&&index>0&& e.key==="Backspace"){
//                 inputAll[index - 1].focus();
//             }
//         })
//     })


  const input1 = [useRef(), useRef(), useRef(), useRef()];
//   otp varification
  function sub_btn() {
    var All = "";
    input1.forEach((i, index) => {
      All += i.current.value;
    });
    if (All.length == "") {
      console.log("please enter the  value!");
    } else {
      console.log(All);
    }
  }
  return (
    <div>
      <div className="container1" id="container1">
        <h2>OTP Varification</h2>
        <div className="inputsAll">
          <input type="number" className="inputs" ref={input1[0]} />
          <input type="number" className="inputs" ref={input1[1]} />
          <input type="number" className="inputs" ref={input1[2]} />
          <input type="number" className="inputs" ref={input1[3]} />
        </div>
        <input type="submit" value="submit" id="sub-btn" onClick={sub_btn} />
      </div>
    </div>
  );
};

export default Varify;
