import React, { useRef, useState } from 'react';
import "./Login.css";

const Login = ({ setLogin }) => {
  const cont1=useRef();
  const cont2=useRef();
  const email=useRef();
  const Aname=useRef();
  const input1=useRef();

   async function sendOtp(){
    let genrateOTP=Math.floor(10000-Math.random()*1000);
    const mail=email.current.value;
    let api= await fetch("/register",{
      method:"POST",
      header:{
      'Content-Type': 'application/json',
      }, body:JSON.stringify({
        mail,
        otp:genrateOTP,
      })
    });
    console.log(api)
    console.log(genrateOTP)
  }


  return (
    <>
    <div className="main">
      <div className="container" ref={cont1}>
        <h2>Log in here</h2>
        <input placeholder='Enter your name' ref={Aname}/>
        <input placeholder='Enter your Email' ref={email}/>
        <input type="submit" value="send" onClick={sendOtp} />
      </div>
      <div>
      <div className="container1" id="container1" ref={cont2}>
        <h2>OTP Varification</h2>
        <div className="inputsAll">
          <input type="number" className="inputs" ref={input1} placeholder=" Enter Your OTP"/>
        </div>
        <input type="submit" value="submit" id="sub-btn" />
      </div>
    </div>
      </div>
      </>
  )
}

export default Login;
