import React from 'react'
import "./Varify.css"

const Varify = () => {
    // 
    let inputs = document.querySelectorAll(".inputs");
    inputs.forEach((input, index) => {
      input.addEventListener("keyup", (e) => {
        if (input.value.length === 1 && index < 3) {
          inputs[index + 1].focus();
        }
        if (input.value.length == 0 && index > 0 && e.key === "Backspace") {
          inputs[index - 1].focus();
        }
      });
    });
    let sub_btn = document.getElementById("sub-btn");
    var All = "";
    sub_btn.addEventListener("click", () => {
      inputs.forEach((data, index) => {
        All += data.value;
      });
    });
    // 
  return (
    <div>
       <div class="container1" id="container1">
        <h2>OTP Varification</h2>
         <div class="inputsAll">
            <input type="number"maxlength="1" class="inputs"/>
            <input type="number"maxlength='1' class="inputs"/>
            <input type="number"maxlength='1' class="inputs"/>
            <input type="number"maxlength='1' class="inputs"/>
         </div>
         <input type="submit" value="submit" id="sub-btn"/>
         </div>
    </div>
  )
}

export default Varify
