import React, { useRef, } from 'react'
import "./Login.css"

const Login = ({setlogin}) => {
  const cont1=useRef();
  const cont2=useRef();

  const input1=useRef()
    const Aname=useRef();
    const email=useRef();
   let generateotp;
      async function sendOtp(){ 
         const c=cont2.current;
         c.style.display="block"
        let mail=email.current.value;
        let name=Aname.current.value;
        if(mail.length===0){
             alert("enter the value") ;
        }
        else{
          // setlogin(false);
          generateotp=Math.floor(10000-Math.random()*1000);
      console.log(generateotp)
          const api=await fetch('/register',
            {
              method:"POST",
              headers:{
                "Content-Type":"application/Json"
              },
              body:JSON.stringify({
                name,
                mail,
                otp:generateotp,
              })
            }
          )
         console.log(api);
        }
    }  

  return (
    <>
    <div className="main">
      <div className="container" ref={cont1}>
        <h2>Log in here</h2>
        <input placeholder='Enter your name' ref={Aname}/>
        <input placeholder='Enter your Email' ref={email}/>
        <input type="submit" value="send" onClick={sendOtp} />
      </div>
      <div>
      <div className="container1" id="container1" ref={cont2}>
        <h2>OTP Varification</h2>
        <div className="inputsAll">
          <input type="number" className="inputs" ref={input1} placeholder=" Enter Your OTP"/>
        </div>
        <input type="submit" value="submit" id="sub-btn" />
      </div>
    </div>
  );
      </div>
      </>
  )
}

export default Login;
