import React, { useRef } from "react";
import { useForm } from "react-hook-form";
import "bootstrap/dist/css/bootstrap.min.css";

const Claim = () => {
  const input1=useRef();
  const cardId1=useRef();
  const policyNumber1=useRef();
  const insuranceCompany1=useRef();
  const hospitalName1=useRef();
  const providerName1=useRef();
  const address1=useRef();
  const claimAmount1=useRef();
  const bankname1=useRef();
  const bankAccountNumber1=useRef();
  const ifscCode1=useRef();
function submitdata(){
 const input=input1.current.value();
  const cardId=cardId1.current.value();
  const policyNumber=policyNumber1.current.value();
  const insuranceCompany=insuranceCompany1.current.value();
  const hospitalName=hospitalName1.current.value();
  const providerName=providerName1.current.value();
  const address=address1.current.value();
  const claimAmount=claimAmount1.current.value();
  const bankname=bankname1.current.value();
  const bankAccountNumber=bankAccountNumber1.current.value();
  const ifscCode=ifscCode1.current.value();
let fetchapi=fetch('http://localhost:9002/Claim_list',{
 method : "POST",
 headers:{
   'Content-Type': 'application/json',
 },body:JSON.stringify({
   name:input,
   cardId:cardId,
   policyNumber:policyNumber,
   insuranceCompany:insuranceCompany,
   hospitalName:hospitalName,
   providerName:providerName,
   address:address,
   claimAmount:claimAmount,
    bankname:bankname,
   bankAccountaNumber:bankAccountNumber,
   ifscCode:ifscCode,
 })
})
console.log(fetchapi);
}




  const { register, formState: { errors } } = useForm();

  

  return (
    <div className="container mt-4">
      <h3 className="text-center">NBCC Claim Form</h3>
      <form>
        {/* Policy Holder Information */}
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            type="text"
            ref={input1}
            className={`form-control ${errors.name && "is-invalid"}`}
            {...register("name", { required: "Name is required" })}
          />
          {errors.name && <div className="invalid-feedback">{errors.name.message}</div>}
        </div>

        <div className="mb-3">
          <label className="form-label">Card ID</label>
          <input
            type="text"
            ref={cardId1}
            className={`form-control ${errors.cardId && "is-invalid"}`}
            {...register("cardId", { required: "Card ID is required" })}
          />
          {errors.cardId && <div className="invalid-feedback">{errors.cardId.message}</div>}
        </div>

        <div className="mb-3">
          <label className="form-label">Policy Number</label>
          <input
            type="text"
            ref={policyNumber}
            className="form-control"
            {...register("policyNumber")}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Insurance Company</label>
          <input
            type="text"
            ref={insuranceCompany}
            className="form-control"
            {...register("insuranceCompany")}
          />
        </div>

        {/* Hospital/Doctor Information */}
        <h5 className="mt-4">Hospital/Doctor's Information</h5>
        <div className="mb-3">
          <label className="form-label">Hospital Name</label>
          <input
            type="text"
            ref={hospitalName}
            className="form-control"
            {...register("hospitalName")}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Provider Code</label>
          <input
            type="text"
            ref={providerName}
            className="form-control"
            {...register("providerCode")}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Address</label>
          <textarea
          ref={address}
            className="form-control"
            {...register("address")}
          ></textarea>
        </div>

        {/* Claim Details */}
        <h5 className="mt-4">Claim Details</h5>
        <div className="mb-3">
          <label className="form-label">Claim Amount</label>
          <input
            type="number"
            ref={claimAmount}
            className={`form-control ${errors.claimAmount && "is-invalid"}`}
            {...register("claimAmount", { required: "Claim amount is required" })}
          />
          {errors.claimAmount && <div className="invalid-feedback">{errors.claimAmount.message}</div>}
        </div>

        <div className="mb-3">
          <label className="form-label">Bank Name</label>
          <input
          ref={bankname}
            type="text"
            className="form-control"
            {...register("bankName")}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Bank Account Number</label>
          <input
          ref={bankAccountNumber}
            type="text"
            className="form-control"
            {...register("bankAccountNumber")}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">IFSC Code</label>
          <input
            type="text"
            ref={ifscCode}
            className="form-control"
            {...register("ifscCode")}
          />
        </div>

        {/* Submission */}
        <button type="submit" className="btn btn-primary w-100"onClick={submitdata}>
          Submit
        </button>
      </form>
    </div>
  )
}

export default Claim;
