import React, { useRef } from "react";
import "./Varify.css";

const Varify = () => {
  const input1 = [useRef(), useRef(), useRef(), useRef()];
//   run all  the inputs
input1.forEach((inputRef, index) => {
    // Add keyup event to each input
    inputRef.current?.addEventListener('keyup', () => {
      // If the length of input is 1 and it's not the last input, move focus to the next input
      if (inputRef.current.value.length === 1 && index < input1.length - 1) {
        input1[index + 1].current.focus();
      }
    });
  });

//   otp varification
  function sub_btn() {
    var All = "";
    input1.forEach((i, index) => {
      All += i.current.value;
    });
    if (All.length == "") {
      console.log("please enter the  value!");
    } else {
      console.log(All);
    }
  }
  return (
    <div>
      <div className="container1" id="container1">
        <h2>OTP Varification</h2>
        <div className="inputsAll">
          <input type="number" className="inputs" ref={input1[0]} onClick={handle} />
          <input type="number" className="inputs" ref={input1[1]} onClick={handle}/>
          <input type="number" className="inputs" ref={input1[2]} onClick={handle}/>
          <input type="number" className="inputs" ref={input1[3]}onClick={handle} />
        </div>
        <input type="submit" value="submit" onClick={sub_btn} />
      </div>
    </div>
  );
};

export default Varify;
