import React, { useRef } from 'react';
import "./Login.css";
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Login = ({setlogin}) => {
  const navigate=useNavigate()
  var genrateOTP;
  const cont1=useRef();
  const cont2=useRef();
  const email=useRef();
  const Aname=useRef();
  const input1=useRef();
   
  function sub_btn(){
    const inp1=input1.current.value;
    const inp2=parseInt(inp1);
    if(inp2===genrateOTP){
      toast.success("OTP Varify successfully!")
      setTimeout(()=>{
      setlogin(true);
        navigate("/home");
      },2500)
    }
    else if(inp1.length===0){
      // alert("failed");
      input1.current.value="";
      toast.error("Enter the  OTP")
    }
    else{
      toast.error('Invalid OTP');
      input1.current.value="";
    }
  }

   async function sendOtp(){
    const name1=Aname.current.value;
    const mail=email.current.value;
    const c1=cont1.current;
    const c2=cont2.current;
    if(name1.length===0){
      // alert("enter your name!")
      toast.error("Enter your Name!")
    }
     else if(mail.length===0){
      toast.error("enter your email")
    }
    
    else if(mail.length>=12) {
     genrateOTP=Math.floor(1000 + Math.random() * 9000);
     c1.style.display="none";
     c2.style.display="block";
     Aname.current.value="";
     email.current.value="";
    toast.success("OTP send successfully!");
    console.log(genrateOTP)
    let api= await fetch("https://backend-server-nryo.onrender.com/register",{
      method:"POST",
      headers:{
      'Content-Type': 'application/json',
      }, body:JSON.stringify({
        mail,
        name:name1,
        otp:genrateOTP,
      })
    });
    console.log(api);
  }
  else{
    toast.error("invalid Email")
  }
}


  return (
    <>
    <div className="main">
    <h2 id='headh2' className='text text-center'>TPA MANAGEMENT</h2>
    <p id='headp' className='text text-center'>TPA connects you with the world and helps you share your moments.</p>
      <div className="Lcontainer" ref={cont1}>
        <h2>Log in here</h2>
        <input type='text' placeholder='Enter your name' ref={Aname}/>
        <input type='email' placeholder='Enter your Email' ref={email}/>
        <input type="submit" value="Send OTP" onClick={sendOtp} />
      </div>
      <div>
      <div className="container1" id="container1" ref={cont2}>
        <h2>OTP Varification</h2>
        <span id='emailspan'>Sent to:<i>{sendEmail}</i></span>
        <div className="inputsAll">
          <input  type="number" className="inputs" ref={input1} placeholder=" Enter Your OTP"/>
        </div>
        <input type="submit" value="submit" id="sub-btn" onClick={sub_btn}/>
      </div>
    </div>
      </div>
      </>
  )
}

export default Login;
